mapper.select:
select COUNT(id)
from feed
where user_id = #{userId}

SELECT T1.fid, T1.create_at, T1.content, T1.f_file_url, IF(IFNULL(t.feed_id, 0) != 0, 1, 0) AS is_thumb, T1.thumb_cnt, T1.uid, T1.nickname, T1.u_file_url, T1.bid, T1.book_name FROM (SELECT u.id AS uid, f.id fid, create_at, content, f.file_url AS f_file_url, nickname, u.file_url AS u_file_url, b.id AS bid, b.book_name, COUNT(t.user_id) AS thumb_cnt, h.score FROM feed f INNER JOIN user u INNER JOIN book b INNER JOIN (SELECT feed_id, sum(MATCH tag AGAINST ('${dynamicQuery}' IN BOOLEAN MODE)) AS score FROM hashtag WHERE MATCH tag AGAINST ('${dynamicQuery}' IN BOOLEAN MODE) GROUP BY feed_id
HAVING COUNT(feed_id) >= #{countOfWords}) AS h
on f.user_id = u.id AND f.book_id = b.id AND f.blocked = 0 AND f.id = h.feed_id left outer join thumb t on t.feed_id = f.id GROUP BY f.id) AS T1 LEFT OUTER JOIN thumb t ON T1.fid = t.feed_id AND #{userId} = t.user_id ORDER BY T1.score DESC, T1.create_at DESC LIMIT 20 OFFSET #{page}
